create PROCEDURE [dbo].[USPCreateOSSOrders] @createOssOrders dbo.CreateOSSOrdersTableType READONLY As Begin -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. Set NoCount On;
Insert Into [OSSOrders] ([THubOrderId] ,[THubOrderReferenceNo] ,[CreatedOn] ,[THubCompleteOrder] ,[OrderStatus]) SELECT [THubOrderId] ,[THubOrderReferenceNo] ,[CreatedOn] ,[THubCompleteOrder] ,[OrderStatus] FROM @createOssOrders
End

CREATE PROCEDURE [dbo].[USPUpdateOrderAfterMoldingBoxShipmentRequest]
-- Add the parameters for the stored procedure here
@OSSOrderId bigint,
@SentToMB bit,
@SentToMBOn datetime,
@MBPostShipmentMessage nvarchar(MAX),
@MBPostShipmentResponseMessage nvarchar(MAX),
@MBSuccessfullyReceived bit,
@MBShipmentId nvarchar(15),
@MBShipmentSubmitError nvarchar(MAX),
@OrderStatus int,
@CancelMessage nvarchar(MAX),
@MBShipmentMethod nvarchar(MAX)

AS
BEGIN
-- SET NOCOUNT ON added to prevent extra result sets from
-- interfering with SELECT statements.
SET NOCOUNT ON;

UPDATE [dbo].[OSSOrders]
SET [SentToMB] = @SentToMB,
[SentToMBOn] = @SentToMBOn,
[MBPostShipmentMessage] = @MBPostShipmentMessage,
[MBPostShipmentResponseMessage] = @MBPostShipmentResponseMessage,
[MBSuccessfullyReceived] = @MBSuccessfullyReceived,
[MBShipmentId] = @MBShipmentId,
[MBShipmentSubmitError] = @MBShipmentSubmitError,
[OrderStatus] = @OrderStatus,
[CancelMessage] = @CancelMessage,
[MBShipmentMethod] = @MBShipmentMethod
WHERE [OSSOrderId] = @OSSOrderId;

END
